# Write your MySQL query statement below
select s.student_id, s.student_name, u.subject_name, count(e.subject_name) as attended_exams from Students s CROSS JOIN Subjects u LEFT JOIN Examinations e ON s.student_id = e.student_id AND u.subject_name = e.subject_name group by u.subject_name, s.student_id, s.student_name order by s.student_id, u.subject_name;